import React, { useState } from "react";

class SolarSystem extends React.Component {
  constructor(props) {
    super(props);
    this.sunRef = React.createRef();
    this.earthRef = React.createRef();
  }

  componentDidMount() {
    const sun = this.sunRef.current;
    const earth = this.earthRef.current;

    TweenMax.to(sun, 10, { rotation: 360, repeat: -1, ease: Linear.easeNone });
    TweenMax.to(earth, 10, { rotation: 360, transformOrigin: '150px 50%', repeat: -1, ease: Linear.easeNone });
  }

  render() {
    return (
      <div>
        <div className="sun" ref={this.sunRef}></div>
        <div className="earth" ref={this.earthRef}></div>
      </div>
    );
  }
}
