import React, { useState, useEffect, Component, useRef} from "react";
import "./SkillSet.scope.css";
import "./saturn.scope.css"
import { Controller, Scene } from 'react-scrollmagic';
import { Tween } from 'react-gsap';
import { gsap, TweenMax } from 'gsap';

const SkillSet = () => {
  const zoomElementRef = useRef(null);
  return (
    <section className="container-page" id='skillset'>
      <div className="">
      <Controller>
        <Scene duration={1000}>
          {(progress) => {
            const scale = 1 + progress; // scale increases from 1 to 2
            gsap.to(zoomElementRef.current, { duration: 0.5, scale: scale });
            return (
              <div className="zoom-wrapper">
                <div className="zoom-element" ref={zoomElementRef}>
                <img className="w-1/3" src='https://res.cloudinary.com/dvehyvk3d/image/upload/v1677338407/SkillSet_1_z38if7.png' alt='skillset'/>
                </div>
              </div>
            );
          }}
        </Scene>
      </Controller>
      </div>
    </section>
  );
};

export default SkillSet;
