export type RevealProps = {
  children: React.ReactNode;
  trigger: React.ReactElement | null;
  repeat: boolean;
  root: Element | null;
  rootMargin: string;
  threshold: number;
};

enum EntryState {
  unknown,
  entered,
  exited,
}

class Reveal extends Provider<RevealProps> {
  static displayName = 'Reveal';

  static defaultProps = {
    trigger: null,
    repeat: false,
    root: null,
    rootMargin: '0px',
    threshold: 0.66,
  };

  timeline: any;
  triggerRef: HTMLElement | null = null;
  observer: IntersectionObserver | null = null;

  init() {
    this.createTimeline();
    this.createIntersectionObserver();
  }

  kill() {
    this.killTimeline();
    this.killIntersectionObserver();
  }

  componentDidMount() {
    this.init();
  }

  componentWillUnmount() {
    this.kill();
  }

  componentDidUpdate(prevProps: RevealProps) {
    const { children, trigger } = this.props;

    // if children change create a new timeline
    // TODO: replace easy length check with fast equal check
    // TODO: same for props.target?
    if (React.Children.count(prevProps.children) !== React.Children.count(children)) {
      this.init();
    }

    if (prevProps.trigger !== trigger) {
      this.init();
    }
  }

  createTimeline() {
    this.killTimeline();

    // init timeline
    this.timeline = gsap.timeline({
      smoothChildTiming: true,
      paused: true,
    });

    // add consumers
    this.consumers.forEach(consumer => {
      const { position } = consumer.props;
      this.timeline.add(consumer.getGSAP().play(), nullishCoalescing(position, 0));
    });
  }

  killTimeline() {
    if (this.timeline) {
      this.timeline.kill();
    }
  }

  createIntersectionObserver() {
    let { root, rootMargin, threshold } = this.props;

    const options = {
      root,
      rootMargin,
      threshold: [0, threshold],
    };

    this.observer = new IntersectionObserver(this.intersectionObserverCallback, options);

    // It would be better if we wouldn't need an extra wrapper.
    // But it can be problematic for example with a fadeInLeft animation
    // were the element is out of the viewport in the initial state.
    // In this case there wouldn't be an intersection..
    if (!this.triggerRef) {
      this.consumers.forEach(consumer => {
        consumer.getTargets().forEach((target: any) => {
          this.observer && this.observer.observe(target);
        });
      });
    } else {
      this.observer && this.observer.observe(this.triggerRef);
    }
  }

  killIntersectionObserver() {
    this.unobserveAll();
    this.observer = null;
  }

  unobserveAll() {
    if (this.observer) {
      if (!this.triggerRef) {
        this.consumers.forEach(consumer => {
          consumer.getTargets().forEach((target: any) => {
            this.observer && this.observer.unobserve(target);
          });
        });
      } else {
        this.observer && this.observer.unobserve(this.triggerRef);
      }
    }
  }

  intersectionObserverCallback = (entries: any) => {
    let { repeat, threshold } = this.props;
    let state: EntryState = EntryState.unknown;

    for (const entry of entries) {
      if (entry.isIntersecting && entry.intersectionRatio >= threshold) {
        this.timeline.play();
        state = EntryState.entered;
        break;
      } else if (!entry.isIntersecting) {
        state = EntryState.exited;
        break;
      }
    }

    if (!repeat && state === EntryState.entered) {
      this.killIntersectionObserver();
    } else if (repeat && state === EntryState.exited) {
      this.timeline.pause(0);
    }
  };

  getGSAP() {
    return this.timeline;
  }

  render() {
    let { children, trigger } = this.props;

    let output = trigger ? (
      <trigger.type {...trigger.props} ref={(trigger: HTMLElement) => (this.triggerRef = trigger)}>
        {children}
      </trigger.type>
    ) : (
      children
    );

    return this.renderWithProvider(output);
  }
}

export default Reveal;
import React from 'react';
import { gsap } from 'gsap';
import { ScrollTrigger as ScrollTriggerPlugin } from 'gsap/dist/ScrollTrigger';
import Provider, { Context } from '../Provider';

gsap.registerPlugin(ScrollTriggerPlugin);

export type ScrollTriggerProps = {
  children?: React.ReactNode;
} & gsap.plugins.ScrollTriggerInstanceVars;

class ScrollTrigger extends Provider<ScrollTriggerProps> {
  static displayName = 'ScrollTrigger';
  static contextType = Context;

  scrollTrigger: any | null = null;
  targets: any = {};

  constructor(props: ScrollTriggerProps) {
    super(props);

    this.getPlugin = this.getPlugin.bind(this);
  }

  // override and pass registerConsumer to next parent provider
  registerConsumer(consumer: any) {
    this.context.registerConsumer(consumer);
  }

  componentDidMount() {
    const { children, ...scrollTrigger } = this.props;

    if (!children) {
      this.scrollTrigger = ScrollTriggerPlugin.create(scrollTrigger);
    }
  }

  componentWillUnmount() {
    if (this.scrollTrigger) {
      this.scrollTrigger.kill();
    }
  }

  // componentDidUpdate(prevProps: ScrollTriggerProps) {
  //   const { trigger } = this.props;
  //
  //   if (trigger !== prevProps.trigger) {
  //     console.log('prevProps.trigger', prevProps.trigger);
  //     console.log('trigger', trigger);
  //   }
  // }

  getGSAP() {
    return this.scrollTrigger;
  }

  getPlugin(props: any, targets: any) {
    let { children, trigger: triggerProp, ...scrollTrigger } = props;

    let trigger = triggerProp;

    if (targets instanceof Map) {
      if (trigger) {
        const target = targets.get(trigger);
        if (target) {
          trigger = target;
        }
      } else {
        trigger = Array.from(targets.values());
      }
    } else if (!trigger) {
      trigger = targets;
    }

    return {
      trigger,
      ...scrollTrigger,
    };
  }

  render() {
    const { children, ...scrollTrigger } = this.props;
    if (!children) {
      return null;
    }
    return this.renderWithProvider(children, {
      scrollTrigger,
    });
  }
}

export default ScrollTrigger;
import React from 'react';

type SplitWordsProps = {
  children: React.ReactNode;
  wrapper: React.ReactElement;
  delimiter?: string;
};

type SplitCharsProps = {
  children: React.ReactNode;
  wrapper: React.ReactElement;
};

const escapeRegExp = (regExp: string) => {
  var specialChars = ['$', '^', '*', '(', ')', '+', '[', ']', '{', '}', '\\', '|', '.', '?', '/'];
  var regex = new RegExp('(\\' + specialChars.join('|\\') + ')', 'g');
  return regExp.replace(regex, '\\$1');
};

// TODO: possible or better to output all the refs as one array?
export const SplitWords = React.forwardRef<any, SplitWordsProps>(
  ({ children, wrapper, delimiter = ' ' }, ref) => {
    if (typeof children !== 'string') {
      throw new Error('SplitWords only accepts a string as child.');
    }
    const words = children.split(new RegExp(`(${escapeRegExp(delimiter)})`, 'g'));
    return (
      <>
        {words.map((word: string, i: number) => {
          if (delimiter === ' ' && word === delimiter) {
            return <React.Fragment key={i}> </React.Fragment>;
          }
          return React.cloneElement(wrapper, { ref, key: i }, word);
        })}
      </>
    );
  }
);

// TODO: possible or better to output all the refs as one array?
export const SplitChars = React.forwardRef<any, SplitCharsProps>(({ children, wrapper }, ref) => {
  if (typeof children !== 'string') {
    throw new Error('SplitChars only accepts a string as child.');
  }
  return (
    <>
      {children
        .split(
          /(?=(?:[\0-\uD7FF\uE000-\uFFFF]|[\uD800-\uDBFF][\uDC00-\uDFFF]|[\uD800-\uDBFF](?![\uDC00-\uDFFF])|(?:[^\uD800-\uDBFF]|^)[\uDC00-\uDFFF]))/
        )
        .map((char: string, i: number) => {
          // TODO: enhance check for space
          if (char === ' ') {
            return <React.Fragment key={i}> </React.Fragment>;
          }
          return React.cloneElement(wrapper, { ref, key: i }, char);
        })}
    </>
  );
});

export const SplitLetters = React.forwardRef((props: any, ref) => {
  console.warn('Deprecation warning: Use SplitChars instead of SplitLetters');
  return <SplitChars {...props} ref={ref} />;
});
import React from 'react';
import { PlayState } from './../types';
import { setPlayState } from './../helper';
import Provider from '../Provider';

type ControlsProps = {
  playState?: PlayState;
};

type ControlsState = {
  totalProgress: number;
  playState?: PlayState;
  prevPlayState?: PlayState;
};

class Controls extends Provider<ControlsProps, ControlsState> {
  gsap: any;
  slider: any;
  sliderTouched: boolean = false;

  state = {
    totalProgress: 0,
    playState: undefined,
    prevPlayState: undefined,
  };

  containerStyle = {
    backgroundColor: '#f0f0f0',
    padding: '10px 10px 0 10px',
    marginTop: '10px',
    position: 'relative' as 'relative',
    zIndex: 2,
    fontFamily: 'verdana, sans-serif',
    fontSize: '16px',
    border: '1px solid #ccc',
  };

  buttonContainerStyle = {
    margin: '0',
    display: 'flex',
    flexWrap: 'wrap' as 'wrap',
    justifyContent: 'space-between',
  };

  buttonStyle = {
    border: '1px solid #999',
    backgroundColor: '#f0f0f0',
    padding: '5px',
    margin: '10px 10px 10px 0',
    cursor: 'pointer',
  };

  sliderStyle = {
    margin: '0',
    width: '100%',
  };

  playStateStyle = {
    color: '#999',
    margin: '10px 0',
    fontSize: '14px',
  };

  componentDidMount() {
    if (this.consumers.length) {
      this.gsap = this.consumers[0];

      const gsap = this.gsap.getGSAP();

      if (gsap) {
        gsap.eventCallback('onUpdate', this.onUpdate);

        if (this.props.playState) {
          this.setPlayState(this.props.playState);
        } else {
          // get child initial state
          if (gsap.paused()) {
            this.setPlayState(PlayState.pause);
          } else if (gsap.reversed()) {
            this.setPlayState(PlayState.reverse);
          } else {
            this.setPlayState(PlayState.play);
          }
        }

        const totalProgress = gsap.totalProgress();
        this.slider.value = totalProgress * 100;
      }
    }
  }

  componentDidUpdate() {
    this.onUpdate();
  }

  onUpdate = () => {
    if (this.gsap && this.slider && !this.sliderTouched) {
      const totalProgress = this.gsap.getGSAP().totalProgress();
      this.slider.value = totalProgress * 100;
    }
  };

  onChange = (event: any) => {
    if (this.gsap && this.gsap.getGSAP()) {
      this.gsap.getGSAP().totalProgress(event.target.value / 100);
    }
  };

  setPlayState = (state: PlayState) => {
    this.setState(prevState => {
      return {
        playState: state,
        prevPlayState: prevState.playState,
      };
    });
  };

  getControls = (_totalProgress: any, playState: PlayState | undefined) => (
    <div style={this.containerStyle}>
      <input
        ref={el => (this.slider = el)}
        type="range"
        style={this.sliderStyle}
        step="0.001"
        onChange={e => this.onChange(e)}
        onMouseDown={() => (this.sliderTouched = true)}
        onMouseUp={() => (this.sliderTouched = false)}
      />
      <div style={this.buttonContainerStyle}>
        <div>
          <button
            type="button"
            style={this.buttonStyle}
            onClick={() => this.setPlayState(PlayState.play)}
          >
            Play
          </button>
          <button
            type="button"
            style={this.buttonStyle}
            onClick={() => this.setPlayState(PlayState.reverse)}
          >
            Reverse
          </button>
          <button
            type="button"
            style={this.buttonStyle}
            onClick={() => this.setPlayState(PlayState.pause)}
          >
            Pause
          </button>
          <button
            type="button"
            style={this.buttonStyle}
            onClick={() => this.setPlayState(PlayState.stop)}
          >
            Stop
          </button>
        </div>
        <span style={this.playStateStyle}>{playState}</span>
      </div>
    </div>
  );

  render() {
    const { children } = this.props;
    const { totalProgress, playState, prevPlayState } = this.state;

    if (this.gsap) {
      setPlayState(playState, prevPlayState, this.gsap.getGSAP());
    }

    return this.renderWithProvider(
      <div>
        {children}
        {this.getControls(totalProgress, playState)}
      </div>
    );
  }
}

export default Controls;
