import React, { useState, useEffect } from "react";
import "./SkillSet.scope.css";
import data from "./SkillsData.js";
import {MdOutlineArrowRightAlt} from 'react-icons/md'

const SkillSlider = () => {
  const [SkillsData] = useState(data);
  const [index, setIndex] = useState(0);

  useEffect(() => {
    const lastIndex = SkillsData.length - 1;
    if (index < 0) {
      setIndex(lastIndex);
    }
    if (index > lastIndex) {
      setIndex(0);
    }
  }, [index, SkillsData]);

  useEffect(() => {
    let slider = setInterval(() => {
      setIndex(index + 1);
    }, 5000);
    return () => {
      clearInterval(slider);
    };
  }, [index]);

  return (
    <section className="container-page">
      {/* <div className="title">
        <h2 className="Skills">Skills</h2>
      </div> */}
      <div className="section-center">
        {SkillsData.map((item, indexSkillsData) => {
          const { id, title, jenkins, k6, k8s, bash, docker, aws, linux, nginx, redis, prometheus, grafana, expressjs, odoo, reactjs, js, python } = item;
          let position = "nextSlide";
          if (indexSkillsData === index) {
            position = "activeSlide";
          }
          if (
            indexSkillsData === index - 1 ||
            (index === 0 && indexSkillsData === SkillsData.length - 1)
          ) {
            position = "lastSlide";
          } 

          return (
            <article className={position} key={id}>
              <p className="title-skillset">{title}</p>
              <svg width="100" height="100">
                <circle cx="50" cy="50" r="40" stroke="green" stroke-width="4" fill="yellow" />
              </svg>
              {/* DevOps */}
              <div className="flex">
                <img className="w-11" src={bash} alt=""/>
                <img className="w-11" src={linux} alt=""/>
                <img className="w-11" src={aws} alt=""/>
                <img className="w-11" src={nginx} alt=""/>
                <img className="w-11" src={redis} alt=""/>
                <img className="w-11" src={docker} alt=""/>
                <img className="w-11" src={jenkins} alt=""/>
                <img className="w-11" src={k8s} alt=""/>
                <img className="w-11" src={prometheus} alt=""/>
                <img className="w-11" src={grafana} alt=""/>
                <img className="w-14" src={k6} alt=""/>
              </div>
              {/* BackEnd */}
              <div className="flex">
                <img className="w-14" src={expressjs} alt=""/>
                <img className="w-14" src={odoo} alt=""/>
              </div>
              {/* FrontEnd */}
              <div className="flex">
                <img className="w-11" src={reactjs} alt=""/>
              </div>
              {/* Programming Language */}
                <div className="flex">
                <img className="w-11" src={js} alt=""/>
                <img className="w-11" src={python} alt=""/>
              </div>
            </article>
          );
        })}
        <MdOutlineArrowRightAlt className="prev" onClick={() => setIndex(index - 1)}>
          <i className="fas fa-arrow-left" />
        </MdOutlineArrowRightAlt>
        <MdOutlineArrowRightAlt className="next" onClick={() => setIndex(index + 1)}>
          <i className="fas fa-arrow-right" />
        </MdOutlineArrowRightAlt>
      </div>
    </section>
  );
};

export default SkillSlider;
