import React, { useState, useEffect, Component, useRef} from "react";
import "./SkillSet.scope.css";
import "./saturn.scope.css"
import { Controller, Scene } from 'react-scrollmagic';
import { TweenMax, Linear } from 'gsap';



const SkillSet = () => {
  const sunRef = useRef(null);
  const earthRef = useRef(null);

  useEffect(() => {
    const sun = sunRef.current;
    const earth = earthRef.current;

    TweenMax.to(sun, 10, { rotation: 360, repeat: -1, ease: Linear.easeNone });
    TweenMax.to(earth, 10, { rotation: 360, transformOrigin: '150px 50%', repeat: -1, ease: Linear.easeNone });
  }, []);

  return (
    <section className="container-page" id='skillset'>
      <div>
      <div className="sun" ref={sunRef}></div>
      <div className="earth" ref={earthRef}></div>
    </div>
    </section>
  );
};

export default SkillSet;
