import React, { useState, useEffect } from "react";
import "./Project.scope.css";
import data from "./ProjectData.js";
import {MdOutlineArrowRightAlt} from 'react-icons/md'

const Slider = () => {
  const [ProjectData] = useState(data);
  const [index, setIndex] = useState(0);

  useEffect(() => {
    const lastIndex = ProjectData.length - 1;
    if (index < 0) {
      setIndex(lastIndex);
    }
    if (index > lastIndex) {
      setIndex(0);
    }
  }, [index, ProjectData]);

  useEffect(() => {
    let slider = setInterval(() => {
      setIndex(index + 1);
    }, 5000);
    return () => {
      clearInterval(slider);
    };
  }, [index]);

  return (
    <section className="section container-page">
      {/* <div className="title">
        <h2 className="project">Project</h2>
      </div> */}
      <div className="section-center">
        {ProjectData.map((item, indexProjectData) => {
          const { id, image, title, description, occupation, url, from, github, icon, tools1, tools2 } = item;
          let position = "nextSlide";
          if (indexProjectData === index) {
            position = "activeSlide";
          }
          if (
            indexProjectData === index - 1 ||
            (index === 0 && indexProjectData === ProjectData.length - 1)
          ) {
            position = "lastSlide";
          }
          return (
            <article className={position} key={id}>
              <p className="title">{title}</p>
              <p className="description text-lg">{description}</p>
              <p className="from text-lg">{from}</p>
              <p className="occupation text-lg">{occupation}</p>
              <a href={url} target="_blank" rel="noreferrer">
                <img className="link-web" src={icon} alt="link-website"/>
              </a>
              <a href={github} target="_blank" rel="noreferrer">
                <img className="github-link" src="https://res.cloudinary.com/dvehyvk3d/image/upload/v1673533111/tech%20stack/github-icon_wq69a4.svg" alt="github"/>
              </a>
              <div className="images">
                  <img src={image} alt={title} className="project-img" />
                    <div className="tools">
                      <img className="w-10" src={tools1} alt="tools"/>
                      <img className="w-10" src={tools2} alt="tools"/>
                    </div>
              </div>
            </article>
          );
        })}
        <MdOutlineArrowRightAlt className="prev" onClick={() => setIndex(index - 1)}>
          <i className="fas fa-arrow-left" />
        </MdOutlineArrowRightAlt>
        <MdOutlineArrowRightAlt className="next" onClick={() => setIndex(index + 1)}>
          <i className="fas fa-arrow-right" />
        </MdOutlineArrowRightAlt>
      </div>
    </section>
  );
};

export default Slider;
