import React, { useState, useEffect, Component, useRef} from "react";
import "./SkillSet.scope.css";
import "./saturn.scope.css"
import { Controller, Scene } from 'react-scrollmagic';
import { Tween } from 'react-gsap';
import { gsap } from 'gsap';

const SkillSet = () => {
  const zoomElementRef = useRef(null);
  return (
    <section className="container-page" id='skillset'>
      <div className="section-center right-60 absolute">
      <Controller>
        <Scene duration={1000}>
          {(progress) => {
            const scale = 1 + progress * 0.5; // scale increases from 1 to 1.5
            const rotation = progress * 360; // rotation increases from 0 to 360 degrees
            const timeline = gsap.timeline();
            timeline.to(zoomElementRef.current, { scale: 1 / scale, rotation });
            return (
              <div className="zoom-wrapper">
                <div className="zoom-element" ref={zoomElementRef} style={{ transformOrigin: 'center center' }} />
                <div className="content" style={{ transform: `scale(${scale})` }}>
                <h1>TEEEEEEEEEST</h1>
                </div>
              </div>
            );
          }}
        </Scene>
      </Controller>
      </div>
    </section>
  );
};

export default SkillSet;
