import React, { useState, useEffect, Component } from "react";
import "./SkillSet.scope.css";
import "./saturn.scope.css"
import { Animator, ScrollContainer, ScrollPage, batch, Fade, FadeIn, FadeOut, Move, MoveIn, MoveOut, Sticky, StickyIn, StickyOut, Zoom, ZoomIn, ZoomOut } from "react-scroll-motion";
import { MotionAnimate } from 'react-motion-animate'

const Spin = (cycle) =>
  ({
    in: {
      style: {
        // `p` is number (0~1)
        // When just before this page appear, `p` will be 0
        // When this page filled your screen, `p` will be 1
        transform: (p) => `rotate(${p * 360 * cycle}deg)`,
      },
    },
    out: {
      style: {
        // `p` is number (0~1)
        // When this page filled your screen, `p` will be 0
        // When just after this page disappear, `p` will be 1
        transform: (p) => `rotate(${p * 360 * cycle}deg)`,
      },
    },
  });

const ZoomInScrollOut = batch(ZoomOut(1,2), Spin())


const SkillSlider = () => {


  return (
    <section className="container-page" id='skillset'>
      <div className="section-center right-60 absolute">
      <MotionAnimate
      variant={{
        hidden: { opacity: 0.2, rotate: -180 },
        show: {
          opacity: 0.8,
          rotate: 0,
          transition: {
            repeat: Infinity,
            duration: 4,
            repeatDelay: 1,
            type: 'spring'
          }
        }
      }}>
        <>or do your own weird thing</>
        </MotionAnimate>
      </div>
    </section>
  );
};

export default SkillSlider;
