import React, { useState, useEffect, Component } from "react";
import "./SkillSet.scope.css";
import "./saturn.scope.css"
import { Controller, Scene } from "react-scrollmagic";
import { TweenMax } from 'react-gsap';

const SkillSlider = () => {
  useEffect(() => {
    const zoomIn = TweenMax.fromTo(
      ".zoom-element",
      1,
      { scale: 1 },
      { scale: 2 }
    );

    const zoomOut = TweenMax.fromTo(
      ".zoom-element",
      1,
      { scale: 2 },
      { scale: 1 }
    );

    const controller = new ScrollMagic.Controller();

    new ScrollMagic.Scene({
      triggerElement: ".zoom-trigger",
      duration: "100%",
      triggerHook: 0.5,
    })
      .setTween(zoomIn)
      .addTo(controller);

    new ScrollMagic.Scene({
      triggerElement: ".zoom-trigger",
      duration: "100%",
      triggerHook: 0.5,
      reverse: true,
    })
      .setTween(zoomOut)
      .addTo(controller);
  }, []);

  return (
    <div className="zoom-trigger">
      <div className="zoom-element">
        <img src="your-image.jpg" alt="your image" />
      </div>
    </div>
  );
}; 


export default SkillSlider;
