import React, { useState, useRef } from "react";

const Carousel = () => {
  const [activeIndex, setActiveIndex] = useState(0);
  const carouselRef = useRef();

  const handleClick = (index) => {
    setActiveIndex(index);
    const carousel = carouselRef.current;
    carousel.style.transform = `rotateY(${index * -60}deg)`;
  };

  return (
    <div className="carousel-container">
      <div className="carousel" ref={carouselRef}>
        <div className="carousel-item">Item 1</div>
        <div className="carousel-item">Item 2</div>
        <div className="carousel-item">Item 3</div>
      </div>
      <div className="carousel-buttons">
        <button onClick={() => handleClick(0)}>1</button>
        <button onClick={() => handleClick(1)}>2</button>
        <button onClick={() => handleClick(2)}>3</button>
      </div>
    </div>
  );
};

export default Carousel;
